create table usuario (
	id int auto_increment primary key,
    email varchar(40),
    senha varchar(40),
	nome varchar(40)
);

create table produto (
	id int auto_increment primary key,
    nome varchar(40),
    armazenamento varchar(40),
	valor varchar(40),
    foto varchar(8000)
);


